INSERT INTO `tbl_word_type` VALUES ('1',' Các từ chỉ những bộ phận cơ bản nhất của cơ thể con người và động vật (đầu, tóc, tai, mắt, mũi, chân, tay, răng,…) '),('2',' Các từ chỉ động tác, hành động cơ bản và thường xuyên của con người, động vật (đi, ăn, ngủ, nằm, chạy, nhảy,…) '),('3',' Các từ chỉ về màu sắc, tính chất hay trạng thái tình cảm (xanh, đỏ, trắng, đen, vui, đau, no, đói,…) '),('4','Các từ gọi tên thực vật, hoặc bộ phận của thực vật nói chung (cây, rễ, lá, trái, …) '),('5','Các từ gọi tên gọi động vật gần gũi, quen thuộc (chó, cá, chim, gà, lợn…) '),('6',' Các từ chỉ những hiện tượng tự nhiên đầu tiên gần gũi với con người (trời, mây, mưa, gió, sấm, chớp, lửa, đất, nước,…)'),('7',' Các từ xưng hô trong gia đình, quan hệ, tôn giáo, tín ngưỡng (ông, bà, bố, mẹ, anh, chị, em, hồn, vía, thần, tiên…..)'),('8',' Các từ gọi tên một số vật dụng, sự vật, thức ăn thiết yếu trong đời sống (muối, cơm, cháo, ru, thịt, rựa, cối, chày, chài, củi, săng, dao,....) '),('9','Các từ chỉ vị trí không gian, thời gian, số đếm (năm, ngày, tháng, tuần, bên phải, bên trái, trước, sau, một, hai, ba, bốn...)');
